pma:database.pma:trigger:
CREATE TRIGGER `after_order_update` AFTER UPDATE ON `order_item` FOR EACH ROW BEGIN -- Restore the previous quantity to inventory UPDATE inventory SET quantity = quantity + OLD.quantity WHERE Inventory_ID = (SELECT Inventory_ID FROM Product WHERE Product_ID = OLD.product_ID);
-- Deduct the new quantity from inventory UPDATE inventory SET quantity = quantity - NEW.quantity WHERE Inventory_ID = (SELECT Inventory_ID FROM Product WHERE Product_ID = NEW.product_ID);
-- Log sold quantity change in Product_Quantity_Updates table INSERT INTO Product_Quantity_Updates (Product_ID, stock_quantity, sold_quantity) VALUES (NEW.product_ID, 0, NEW.quantity - OLD.quantity); END

CREATE TRIGGER `before_order_insert` BEFORE INSERT ON `order_item`
 FOR EACH ROW BEGIN
  DECLARE available_quantity DECIMAL(10,2);

  SELECT quantity INTO available_quantity
  FROM inventory
  WHERE Inventory_ID = (SELECT Inventory_ID FROM Product WHERE Product_ID = NEW.product_ID);

  IF available_quantity < NEW.quantity THEN
    SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Not enough inventory available';
  END IF;
END
